
public class LocalVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		float b;
		boolean c;
		double d;
		a=100;
		b=99.9f;
		c=true;
		d=100.99;
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);
		

	}

}
